function initEdit(){$("h2.delete").on("click",function(){confirm("Вы уверены, что хотите удалить данное изображение?")&&deleteImage($(this).attr("data-id"))})}function deleteImage(n){$.ajax({url:"/api/images/"+n,type:"DELETE",success:function(){var t=$('h2.delete[data-id="'+n+'"]');t.closest("div.project-image").remove();t.hasClass("main-image")&&$("input#ImageId").val("")},error:handleException})}function handleException(n,t,i){console.log(t,i);alert("При удалении изображения произошла ошибка. Попробуйте еще раз.")}